import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DeliveryDetails from '../components/DeliveryDetails';
import OrderList from '../components/OrderList';
import PromoCode from '../components/PromoCode';
import OrderSummary from '../components/OrderSummary';
import { Icon } from '@iconify/react';

const Checkout = () => {
  const [orderDetails, setOrderDetails] = useState({ products: [] });

  useEffect(() => {
    axios.get('https://groww-intern-assignment.vercel.app/v1/api/order-details#')
      .then(response => {
        console.log('API Response:', response.data);
        setOrderDetails(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="absolute flex flex-col items-center justify-center left-0 top-0 right-0 bottom-0">
      <div className='w-96 flex justify-around bg-white p-2 pt-6'>
        <Icon icon="mingcute:left-line" color="#6d30ed" className='basis-1/8 -ml-10 cursor-not-allowed' width="24" height="24" />
        <p className='font-bold text-mid-black text-lg justify-center basis-1/2'>Checkout</p>
      </div>
      <DeliveryDetails />
      <OrderList orderDetails={orderDetails} />
      <PromoCode />
      <OrderSummary orderDetails={orderDetails} />
    </div>
  );
};

export default Checkout;
