import React, { useState } from 'react';

const AutoCompleteChips = () => {
  const [inputValue, setInputValue] = useState('');
  const [selectedItems, setSelectedItems] = useState([]);
  const [availableItems, setAvailableItems] = useState([
    { label: 'Angular', imageUrl: 'https://xsgames.co/randomusers/avatar.php?g=pixel' },
    { label: 'jQuery', imageUrl: 'https://xsgames.co/randomusers/avatar.php?g=pixel' },
    { label: 'Polymer', imageUrl: 'https://xsgames.co/randomusers/avatar.php?g=pixel' },
    { label: 'React', imageUrl: 'https://xsgames.co/randomusers/avatar.php?g=pixel4' },
    { label: 'Vue.js', imageUrl: 'https://xsgames.co/randomusers/avatar.php?g=pixel5' },
    { label: 'NextJS', imageUrl: 'https://xsgames.co/randomusers/avatar.php?g=pixel6' },
  ]);

  const getRandomImageUrl = (index) =>
    `https://xsgames.co/randomusers/avatar.php?g=pixel${index + 1}`;

  const handleInputChange = (e) => {
    setInputValue(e.target.value);

    if (e.key === 'Enter' && availableItems.length > 0) {
      handleItemClick(availableItems[0]);
    }
  };

  const handleItemClick = (item) => {
    const newItem = { label: item.label, imageUrl: item.imageUrl };
    setSelectedItems([...selectedItems, newItem]);
    setAvailableItems(availableItems.filter((i) => i.label !== item.label));
    setInputValue('');
  };

  const handleChipDelete = (item) => {
    setSelectedItems(selectedItems.filter((i) => i.label !== item.label));
    setAvailableItems([...availableItems, item]);
  };

  const showAllItems = () => {
    setInputValue('');
  };

  return (
    <div className="relative w-full h-full flex flex-col items-center justify-center">
      <div className="relative">
        <div className="border border-b-blue-500 flex relative z-10 w-fit">
          <div className="flex">
            {selectedItems.map((item) => (
              <div key={item.label} className="m-1">
                <div className="flex items-center bg-white border-2 rounded-full px-3 py-2">
                  <img
                    src={item.imageUrl}
                    alt="avatar"
                    className="w-6 h-6 rounded-full mr-2"
                  />
                  <span className="text-sm">{item.label}</span>
                  <button
                    onClick={() => handleChipDelete(item)}
                    className="ml-1 text-sm font-semibold focus:outline-none hover:text-red-600"
                  >
                    X
                  </button>
                </div>
              </div>
            ))}
          </div>
          <input
            id="autocompleteInput"
            type="text"
            value={inputValue}
            onChange={handleInputChange}
            onKeyDown={handleInputChange}
            onFocus={showAllItems}
            placeholder="Type to enter..."
            className="w-full py-2 px-4 bg-app-bg outline-none"
          />
        </div>
        {(inputValue || document.activeElement === document.getElementById('autocompleteInput')) && (
          <div className="absolute top-full right-0 bg-white border rounded-md mt-1 w-40 shadow-md z-20">
            {availableItems
              .filter((item) => item.label.toLowerCase().includes(inputValue.toLowerCase()))
              .map((item, index) => (
                <div
                  key={item.label}
                  onClick={() => handleItemClick(item)}
                  className="p-2 cursor-pointer hover:bg-gray-200 flex items-center"
                >
                  <img
                    src={getRandomImageUrl(index)}
                    alt="avatar"
                    className="w-6 h-6 rounded-full mr-2"
                  />
                  {item.label}
                </div>
              ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AutoCompleteChips;
