import React, { useState } from 'react';

const AutoCompleteChips = () => {
  const [inputValue, setInputValue] = useState('');
  const [selectedItems, setSelectedItems] = useState([]);
  const [availableItems, setAvailableItems] = useState([
    'Angular',
    'jQuery',
    'Polymer',
    'React',
    'Vue.js',
    'NextJS',
  ]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);

    if (e.key === 'Enter' && availableItems.length > 0) {
      handleItemClick(availableItems[0]);
    }
  };

  const handleItemClick = (item) => {
    setSelectedItems([item, ...selectedItems]);
    setAvailableItems(availableItems.filter((i) => i !== item));
    setInputValue('');
  };

  const handleChipDelete = (item) => {
    setSelectedItems(selectedItems.filter((i) => i !== item));
    setAvailableItems([...availableItems, item]);
  };

  const showAllItems = () => {
    setInputValue('');
  };

  return (
    <div className="relative w-full h-full flex flex-col items-center justify-center">
      <div className="relative ">
        <div className="border border-b-indigo-500 relative z-10 w-fit">
          <div className="flex flex-wrap-reverse">
            {selectedItems.map((item) => (
              <div key={item} className="m-1">
                <div className="flex items-center bg-app-bg px-3 py-2">
                  <span className="text-sm">{item}</span>
                  <button
                    onClick={() => handleChipDelete(item)}
                    className="ml-1 text-sm font-semibold focus:outline-none hover:text-red-600"
                  >
                    X
                  </button>
                </div>
              </div>
            ))}
          </div>
          <input
            id="autocompleteInput"
            type="text"
            value={inputValue}
            onChange={handleInputChange}
            onKeyDown={handleInputChange}
            onFocus={showAllItems}
            placeholder="Type to search..."
            className="w-full py-2 px-4 bg-app-bg"
          />
        </div>
        {(inputValue || document.activeElement === document.getElementById('autocompleteInput')) && (
          <div className="absolute top-full left-0 bg-white border rounded-md mt-1 w-40 shadow-md z-20">
            {availableItems
              .filter((item) => item.toLowerCase().includes(inputValue.toLowerCase()))
              .map((item) => (
                <div
                  key={item}
                  onClick={() => handleItemClick(item)}
                  className="p-2 cursor-pointer hover:bg-gray-200"
                >
                  {item}
                </div>
              ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AutoCompleteChips;
