import React, { useState } from 'react';

const AutoCompleteChips = () => {
  const [inputValue, setInputValue] = useState('');
  const [selectedItems, setSelectedItems] = useState([]);
  const [availableItems, setAvailableItems] = useState([
    'Angular',
    'jQuery',
    'Polymer',
    'React',
    'Vue.js',
    'NextJS',
  ]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleItemClick = (item) => {
    setSelectedItems([...selectedItems, item]);
    setAvailableItems(availableItems.filter((i) => i !== item));
    setInputValue('');
  };

  const handleChipDelete = (item) => {
    setSelectedItems(selectedItems.filter((i) => i !== item));
    setAvailableItems([...availableItems, item]);
  };

  const showAllItems = () => {
    setInputValue('');
  };

  return (
    <div className="w-full h-full flex flex-col items-center justify-center">
      <div className="relative">
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          onFocus={showAllItems}
          placeholder="Type to search..."
          className="w-96 border p-2 rounded-md"
        />
        {(inputValue || document.activeElement === document.getElementById('autocompleteInput')) && (
          <div className="absolute top-10 bg-white border rounded-md mt-1 w-40 shadow-md">
            {availableItems
              .filter((item) => item.toLowerCase().includes(inputValue.toLowerCase()))
              .map((item) => (
                <div
                  key={item}
                  onClick={() => handleItemClick(item)}
                  className="p-2 cursor-pointer hover:bg-gray-200"
                >
                  {item}
                </div>
              ))}
          </div>
        )}
      </div>
      <div className="flex flex-wrap mt-4">
        {selectedItems.map((item) => (
          <div key={item} className="m-1">
            <div className="flex items-center border rounded-md px-3 py-1 bg-gray-200">
              <span className="text-sm">{item}</span>
              <button
                onClick={() => handleChipDelete(item)}
                className="ml-1 text-sm font-semibold focus:outline-none hover:text-red-600"
              >
                X
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AutoCompleteChips;
