import React, { useState, useEffect } from 'react';
import logo from '../assets/logo.svg';
import tbo from '../assets/tbo.svg';
import SearchForm from '../components/SearchForm';

const Medical = () => {
  const [displayText, setDisplayText] = useState("Searchclan is one-stop solution for all your travel voes!");
  const [showSearchForm, setShowSearchForm] = useState(false);

  useEffect(() => {
    const textTimeoutId = setTimeout(() => {
      setDisplayText("You can even try out Istanbul");
    }, 20000); // 20 seconds

    const formTimeoutId = setTimeout(() => {
      setShowSearchForm(true);
    }, 15000); // 15 seconds

    // Cleanup the timeouts to avoid memory leaks
    return () => {
      clearTimeout(textTimeoutId);
      clearTimeout(formTimeoutId);
    };
  }, []); // Empty dependency array ensures the effect runs only once

  return (
    <div className='h-full w-full overflow-auto medical-bg'>
      <div className='h-1/10 m-2 flex justify-between'>
        <img src={logo} alt='LOGO' className='w-32 mx-12' />
        <div className='w-1/3 h-12 flex mt-8 bg-white rounded-3xl shadow-lg overflow-hidden'>
          <div className='px-12 py-3 uppercase animate-marquee whitespace-nowrap overflow-hidden'>
            {displayText}
          </div>
        </div>
        <img src={tbo} alt='TBO' className='w-40 mx-12' />
      </div>
      {showSearchForm && (
        <div className='mt-20'>
          <SearchForm sector='medical' />
        </div>
      )}
    </div>
  );
};

export default Medical;
