import React, { useState } from 'react';

const Chip = ({ label, onDelete }) => {
  return (
    <div className="inline-block m-1">
      <div className="flex items-center border rounded-md px-3 py-1 bg-gray-200">
        {label === 'React' && <span className="mr-1">R</span>}
        <span className="text-sm">{label}</span>
        {onDelete && (
          <button
            onClick={onDelete}
            className="ml-1 text-sm font-semibold focus:outline-none hover:text-red-600"
          >
            X
          </button>
        )}
      </div>
    </div>
  );
};

const ChipsArray = () => {
  const [chipData, setChipData] = useState([
    { key: 0, label: 'Angular' },
    { key: 1, label: 'jQuery' },
    { key: 2, label: 'Polymer' },
    { key: 3, label: 'React' },
    { key: 4, label: 'Vue.js' },
    { key: 5, label: 'NextJS' },
  ]);

  const handleDelete = (key) => {
    setChipData((chips) => chips.filter((chip) => chip.key !== key));
  };

  return (
    <div className="flex flex-col items-center mt-10">
      <div className="flex flex-wrap mb-3">
        {chipData.map((data) => (
          <Chip key={data.key} label={data.label} onDelete={() => handleDelete(data.key)} />
        ))}
      </div>
      <input
        type="text"
        placeholder="Add a chip..."
        className="border rounded-md px-3 py-2 focus:outline-none"
      />
    </div>
  );
};

export default ChipsArray;
