import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Icon } from '@iconify/react';
import { Link } from 'react-router-dom';

const OrderList = () => {
  const [orderDetails, setOrderDetails] = useState({ products: [] });

  useEffect(() => {
    // Fetch data from the API using Axios
    axios.get('https://groww-intern-assignment.vercel.app/v1/api/order-details#')
      .then(response => {
        console.log('API Response:', response.data);
        setOrderDetails(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="w-96 h-fit z-100 bg-white shadow-lg p-2">
      <div className="flex flex-col justify-center p-4">
        <p className='text-dark-gray font-medium text-sm'>Order List</p>

        {orderDetails.products && orderDetails.products.map((product, index) => (
          <div key={index} className='w-full font-light text-dark-gray rounded mt-4 flex p-2 items-center justify-around border'>
            <div className='basis-1/4'>
              <img src={product.image} alt={product.title} className='h-8 w-8 rounded-lg' />
            </div>
            <div className='flex flex-col basis-1/2'>
              <span className='text-sm text-dark-gray font-semibold text-clip'>{product.title}</span>
              <span className='text-sm text-dark-gray mt-1'>{product.price}</span>
            </div>
            <div className='flex gap-6 basis-1/4'>
              <Icon icon="akar-icons:minus" color="#17181a" width="20" height="20" />
              <Icon icon="ion:star-outline" color="#17181a" width="20" height="20" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrderList;
