import React from 'react';
import { Link } from 'react-router-dom';
const OrderSummary = ({ orderDetails }) => {
  const truncateTitle = (title) => {
    const words = title.split(' ');
    const truncatedTitle = words.slice(0, 3).join(' ');
    return truncatedTitle;
  };

  const orderAmount = orderDetails.products.reduce((total, product) => total + product.price, 0);

  return (
    <div className="w-96 h-fit z-100 bg-white shadow-lg p-2">
      <div className="flex flex-col justify-center p-4">
        <p className='text-dark-gray font-bold text-lg'>Order Summary</p>
        <div className='w-full rounded mt-4 flex flex-col p-2 items-center justify-around '>
          {orderDetails.products.map((product, index) => (
            <div key={index} className='w-full flex'>
              <span className='basis-1/2 justify-start text-dark-gray text-sm'>{truncateTitle(product.title)}</span>
              <span className='basis-1/2 justify-end text-right text-gray-400 font-light text-sm'>{product.price}</span>
            </div>
          ))}
          <div className='w-full flex'>
            <span className='basis-1/2 justify-start text-dark-gray text-sm'>Delivery Fee</span>
            <span className='basis-1/2 justify-end text-right text-gray-400 font-light text-sm'>10.000</span>
          </div>
          <div className='w-full flex'>
            <span className='basis-1/2 justify-start text-dark-gray text-sm'>Discount</span>
            <span className='basis-1/2 justify-end text-right text-gray-400 font-light text-sm'>10.000</span>
          </div>
        </div>
      </div>
      <div className='flex p-4 sticky'>
        <div className='flex flex-col basis-1/2'>
          <span className='uppercase'>TOTAL</span>
          <span className='font-bold mt-2'>{orderAmount}</span>
        </div>
        <div className='basis-1/2 w-full justify-items-end items-center'>
          <Link to='/payments'>
            <button className='w-4/5 bg-app-bg justify-self-end items-center py-1.5 px-6 rounded text-white mt-4 ml-8'>
              Payment
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default OrderSummary;
